import "regenerator-runtime/runtime";
import axios from "axios";

// ...

const BASE_URL = "http://localhost:3300/api/users";

const getTodoItems = async () => {
  try {
    const response = await axios.get(`${BASE_URL}`);

    const todoItems = response.data;
    console.log(response.data);

    console.log(`GET: Here's the list of todos`, todoItems);

    return todoItems;
  } catch (errors) {
    console.error(errors);
  }
};

// ...

// ...

const createTodoElement = (item) => {
  //   console.log(item);
  const todoElement = document.createElement("li");

  todoElement.id = item.customer_id;
  //   console.log(todoElement);
  todoElement.appendChild(document.createTextNode(item.name));
  todoElement.appendChild(document.createTextNode(item.vat_number));
  todoElement.appendChild(document.createTextNode(item.email));
  console.log(todoElement);

  todoElement.onclick = async (event) =>
    await removeTodoElement(event, todoElement);

  return todoElement;
};

const updateTodoList = (todoItems) => {
  const todoList = document.querySelector("ul");
  console.log(Array.isArray(todoItems));
  if (Array.isArray(todoItems) && todoItems.length > 0) {
    todoItems.map((todoItem) => {
      todoList.appendChild(createTodoElement(todoItem));
    });
  } else if (todoItems) {
    todoList.appendChild(createTodoElement(todoItems));
  }
};

const main = async () => {
  updateTodoList(await getTodoItems());
};

main();

////// This code will take the value from the form and create a todo object
const form = document.querySelector("form");

form.addEventListener("submit", async (event) => {
  event.preventDefault();

  const title = document.querySelector("#new-todos__title").value;

  const todo = {
    userId: 1,
    title: title,
    completed: false,
  };

  const submitTodoItem = await addTodoItem(todo);
  updateTodoList(submitTodoItem);
});

////Next, you will need to define the addTodoItem function
export const addTodoItem = async (todo) => {
  try {
    const response = await axios.post(`${BASE_URL}/todos`, todo);
    const newTodoItem = response.data;

    console.log(`Added a new Todo!`, newTodoItem);

    return newTodoItem;
  } catch (errors) {
    console.error(errors);
  }
};

//// delete request

export const deleteTodoItem = async (id) => {
  try {
    const response = await axios.delete(`${BASE_URL}/todos/${id}`);
    console.log(`Deleted Todo ID: `, id);

    return response.data;
  } catch (errors) {
    console.error(errors);
  }
};

/////removeTodoElement function:

/////This code will remove the element from the DOM and then call deleteTodoItem with the id.

const removeTodoElement = async (event, element) => {
  event.target.parentElement.removeChild(element);
  const id = element.id;

  await deleteTodoItem(id);
};
